mapper.select:
select
  express_id, address_id, express_company, courier_number, sponsor_delivery_time, confirmer_requesttime, receivingtime_of_initiator, confirmation_request_receiving_time, yuliu
from yiwu.express


        and express_id = #{expressId}


        and address_id = #{addressId}


        and express_company = #{expressCompany}


        and courier_number = #{courierNumber}


        and sponsor_delivery_time = #{sponsorDeliveryTime}


        and confirmer_requesttime = #{confirmerRequesttime}


        and receivingtime_of_initiator = #{receivingtimeOfInitiator}


        and confirmation_request_receiving_time = #{confirmationRequestReceivingTime}


        and yuliu = #{yuliu}
