mapper.select:
select
  address_id, user_id, address_state, addr_province, addr_city, addr_area, addr_detail, user_name, user_cellphone, addresscreate, yuliu
from yiwu.shippingaddress


        and address_id = #{addressId}


        and user_id = #{userId}


        and address_state = #{addressState}


        and addr_province = #{addrProvince}


        and addr_city = #{addrCity}


        and addr_area = #{addrArea}


        and addr_detail = #{addrDetail}


        and user_name = #{userName}


        and user_cellphone = #{userCellphone}


        and addresscreate = #{addresscreate}


        and yuliu = #{yuliu}
